import { ComponentRef } from '@angular/core';
/**
 * 弹框组件生成配置参数接口
 *
 * 该接口用于弹框组件[open]{@link TiModalService#open}方法的参数的定义
 */
export interface TiModalConfig {
    /**
     * 弹框id，设置id可防止同一页面生成多个相同弹框的问题
     */
    id?: string;
    /**
     * 头部内容水平对齐位置
     *
     * @default 'left'
     */
    headerAlign?: 'center' | 'left' | 'right';
    /**
     * 头部是否有关闭按钮
     *
     * @default true
     */
    closeIcon?: boolean;
    /**
     * 弹框样式定义，该样式定义在弹框整体元素上，可通过该样式定义弹框的宽高等
     */
    modalClass?: string;
    /**
     * 控制模态背景的显示，true:显示模态，false:不显示模态，
     * @default true
     */
    backdrop?: boolean;
    /**
     * 控制弹窗是否可拖拽，true:可拖拽，false:不可拖拽，
     * @default true
     */
    draggable?: boolean;
    /**
     * 控制弹窗显示/隐藏是否带动画效果，true:带动效，false:不带动效
     * @default true
     */
    animation?: boolean;
    /**
     * 弹窗内容上下文，为自定义对象形式
     */
    context?: any;
    /**
     * 控制是否可通过ESC快捷键关闭弹窗，true:可快捷关闭，false:不可快捷关闭
     * @default true
     */
    closeOnEsc?: boolean;
    /**
     * 关闭弹窗前的回调函数,可通过beforeClose事件阻止弹框关闭:在beforeClose中通过modalRef调用destroy时，关闭弹框，否则不关闭
     *
     * **函数类型：**(modalRef: TiModalRef, reason: boolean) => void;
     *
     * **参数：**
     *
     * 弹框实例 TiModalRef
     *
     * 弹框关闭原因 boolean
     *
     * 调用close关闭时，reason为true；调用dismiss关闭时，reason为false（点右上角X关闭时为false）
     */
    beforeClose?(modalRef: TiModalRef, reason: boolean): void;
    /**
     * 通过close方法调用弹框关闭弹窗的事件回调（一般点确认时会进入该回调）
     *
     * **函数类型：**(modalRef: TiModalRef) => void;
     *
     * **参数：** 弹框实例 TiModalRef
     */
    close?(modalRef: TiModalRef): void;
    /**
     * 通过dismiss方法调用弹框关闭弹窗的事件回调（一般点击取消或右上角X关闭时会进入该回调）
     *
     * **函数类型：**(modalRef: TiModalRef) => void;
     *
     * **参数：** 弹框实例 TiModalRef
     */
    dismiss?(modalRef: TiModalRef): void;
}
/**
 * 弹框组件实例对象接口
 *
 * 该接口用于弹框组件[open]{@link TiModalService#open}方法的返回值定义
 */
export interface TiModalRef {
    /**
     * 弹框内容实例，适用于component形式的内容数据获取，具体用法可参考示例
     */
    content: ComponentRef<any>;
    /**
     * @ignore
     */
    _id: string;
    /**
     * 弹窗关闭方法，主要用在点击"确认(OK)"按钮时关闭弹框
     *
     * **函数类型：**() => void;
     */
    close(): void;
    /**
     * 弹窗关闭方法，主要用在点击"取消(Cancel)"或右上方X按钮时关闭弹框
     *
     * **函数类型：**() => void;
     */
    dismiss(): void;
    /**
     * 弹框销毁方法
     *
     * **函数类型：**(reason: boolean) => void;
     *
     * **参数：**
     *
     * 弹框关闭原因 boolean，同beforeClose参数中reason的意义
     */
    destroy(reason: boolean): void;
    /**
     * @ignore
     * 弹框销毁方法，无关取消/关闭事件等
     */
    _remove(): void;
}
