/**
 * 问卷调查组件配置接口
 */
export interface TpFeedbackOptions {
    /**
     * 服务名称
     */
    serviceName: string;
    /**
     * 服务Id，各个服务具有唯一性，方便后续进行数据分析
     */
    serviceId: string;
    /**
     * 新增题目的数组（基于已有题目进行新增）
     */
    customQuestion?: Array<TpQuestionOption>;
    /**
     * 问卷内容全部自定义，与customQuestion互斥（二选一）
     */
    allQuestion?: TpAllquestionOption;
    /**
     * 允许有多余的属性字段
     */
    [propName: string]: any;
}
/**
 * 用户自定义题目（基于已有题目）配置接口
 */
export interface TpQuestionOption {
    /**
     * customquestion 有radio、checkbox两种类型
     *
     * allquestion 有radio、checkbox、essay三种类型
     */
    type?: string;
    /**
     * 题目所有标题，包含主标题和副标题
     */
    title?: string;
    /**
     * 是否是必选题目
     */
    require?: boolean;
    /**
     * 设置题目初始是否显示
     */
    show?: boolean;
    /**
     * 设置是否有其它原因（多选题目checkbox需要配置）
     */
    hasOtherReason?: boolean;
    /**
     * 多选题目checkbox的选项（对象数组），可设置两个属性，value、remark两个属性
     */
    options: {
        value: string;
        remark: string;
    };
    /**
     * 允许有多余的属性字段
     */
    [propName: string]: any;
}
/**
 * 用户自定义所有题目配置接口
 */
export interface TpAllquestionOption {
    /**
     * 问卷的标题
     */
    title?: string;
    /**
     * 问卷标题的描述
     */
    startDesc?: string;
    /**
     *  问卷题目列表
     */
    question?: Array<TpQuestionOption>;
    /**
     * 允许有多余的属性字段
     */
    [propName: string]: any;
}
